target.pgsql:
CREATE SEQUENCE "seq_post" start 1 increment 1 maxvalue 2147483647 minvalue 1  cache 1 ;
GRANT ALL on "seq_post" to "postgres";
GRANT ALL on "seq_post" to "${db.iwethey.user}";

target.pgplsql:
CREATE FUNCTION f_post_insert() RETURNS trigger AS '
DECLARE
	p_level integer;
	p_right integer;
	nowtime timestamp(3);
BEGIN
	nowtime := CURRENT_TIMESTAMP;

	IF NEW.post_parent_id IS NULL THEN
		p_level := 0;
		p_right := 1;
	ELSE
		SELECT post_level, post_right_id INTO p_level, p_right
		  FROM post
		 WHERE post_id = NEW.post_parent_id;

		UPDATE post
		   SET post_left_id = CASE WHEN post_left_id > p_right THEN post_left_id + 2 ELSE post_left_id END,
		       post_right_id = post_right_id + 2
		 WHERE post_right_id >= p_right
		   AND post_thread_id = NEW.post_thread_id;
	END IF;

	NEW.post_level := p_level + 1;
	NEW.post_left_id := p_right;
	NEW.post_right_id := p_right + 1;

	NEW.post_created := nowtime;
	NEW.post_last_updated := nowtime;
	NEW.post_child_last_updated := nowtime;

	NEW.post_subject_idx := to_tsvector(NEW.post_subject);
	NEW.post_content_idx := to_tsvector(NEW.post_content);
	NEW.post_signature_idx := to_tsvector(NEW.post_signature);

	UPDATE post
	   SET post_child_last_updated = nowtime
	 WHERE post_id = NEW.post_thread_id;

	UPDATE forum
	   SET forum_post_count = forum_post_count + 1,
	       forum_last_updated = nowtime
	 WHERE forum_id = NEW.post_forum_id;

	IF NEW.post_parent_id IS NULL THEN
		UPDATE forum
		   SET forum_thread_count = forum_thread_count + 1
		 WHERE forum_id = NEW.post_forum_id;
        END IF;

	RETURN NEW;
END;
' LANGUAGE plpgsql
;

CREATE TRIGGER post_insert BEFORE INSERT ON post FOR EACH ROW EXECUTE PROCEDURE f_post_insert()
;

target.pgsql:
      CREATE TABLE "post" (
      	"post_id" int NOT NULL DEFAULT nextval('seq_post') PRIMARY KEY,
      	"post_forum_id" int NOT NULL REFERENCES forum (forum_id) ON DELETE CASCADE ON UPDATE CASCADE,
      	"post_parent_id" int NULL REFERENCES post (post_id),
      	"post_thread_id" int NULL REFERENCES post (post_id),
      	"post_is_thread" boolean default 'f'::bool,
      	"post_left_id" int NOT NULL DEFAULT 0,
      	"post_right_id" int NULL DEFAULT 0,
      	"post_level" int NULL DEFAULT 0,
      	"post_subject" text NOT NULL,
	"post_subject_idx" tsvector,
      	"post_content" text NOT NULL,
	"post_content_idx" tsvector,
      	"post_content_orig" text NOT NULL,
      	"post_signature" text NOT NULL,
	"post_signature_idx" tsvector,
      	"post_signature_orig" text NOT NULL,
	"post_locked" boolean default 'f'::bool,
	"post_convert_newlines" boolean default 't'::bool,
	"post_convert_links" boolean default 't'::bool,
	"post_convert_codes" boolean default 't'::bool,
	"post_convert_html" boolean default 't'::bool,
      	"post_created_by" int NOT NULL REFERENCES iwethey_user (user_id) ON UPDATE CASCADE,
      	"post_created" timestamp(3) with time zone DEFAULT CURRENT_TIMESTAMP NOT NULL,
      	"post_last_updated_by" int NULL REFERENCES iwethey_user (user_id) ON UPDATE CASCADE,
      	"post_last_updated" timestamp(3) with time zone DEFAULT CURRENT_TIMESTAMP NOT NULL,
      	"post_child_last_updated" timestamp(3) with time zone DEFAULT CURRENT_TIMESTAMP NOT NULL
      ) WITHOUT OIDS;

      CREATE INDEX idxpost ON post USING btree (post_id);
      CREATE INDEX idxsubject ON post USING gist (post_subject_idx);
      CREATE INDEX idxcontent ON post USING gist (post_content_idx);
      CREATE INDEX idxsignature ON post USING gist (post_signature_idx);
      CREATE INDEX idxpostlast ON post USING btree (post_forum_id, post_last_updated, post_child_last_updated);
      CREATE INDEX idxpostthread ON post USING btree (post_forum_id, post_is_thread, post_id);
      CREATE INDEX idxpostforum ON post USING btree (post_forum_id, post_thread_id, post_id);

      REVOKE ALL on "post" from PUBLIC;
      GRANT ALL on "post" to "postgres";
      GRANT ALL on "post" to "${db.iwethey.user}";

      CREATE TABLE "post_history" (
      	"post_history_parent_id" int NOT NULL REFERENCES post (post_id) ON DELETE CASCADE ON UPDATE CASCADE,
      	"post_history_order" int NOT NULL,
      	"post_history_subject" text NOT NULL,
      	"post_history_content" text NOT NULL,
      	"post_history_signature" text NOT NULL,
      	"post_history_created_by" int NOT NULL REFERENCES iwethey_user (user_id) ON UPDATE CASCADE,
      	"post_history_created" timestamp(3) with time zone DEFAULT CURRENT_TIMESTAMP NOT NULL
      ) WITHOUT OIDS;

      CREATE INDEX idxposthistory ON post_history USING btree (post_history_parent_id, post_history_order);

      REVOKE ALL on "post_history" from PUBLIC;
      GRANT ALL on "post_history" to "postgres";
      GRANT ALL on "post_history" to "${db.iwethey.user}";

DROP TRIGGER post_update ON post CASCADE;
DROP TRIGGER post_insert ON post CASCADE;
DROP FUNCTION f_post_update() CASCADE;
DROP FUNCTION f_post_insert() CASCADE;

